/*
 * 1. Allow containers to shrink and grow
 * 2. IE Fallback
 */

.layout {
  display: flex;
  flex-direction: column;
  &__cell {
    flex: 0 1 100%;
    max-width: 100%; /* 2. */
  }
  &--reverse {
    flex-direction: column-reverse
  }
  &--even {
    flex: 0 0 auto !important;
    max-width: 100%; /* 2. */
  }
  &--align-middle {
    align-items: center;
  }
}

@include breakpoint( $gt-small-desktop,$msie ) {
  .layout__cell {
    flex: 0 1 100%;
    max-width: 100%; /* 2. */
  }
}


@include breakpoint($gt-small-desktop) {
  .layout {
    flex-direction: row;
    &__cell {
      // flex-shrink: 1; /* 1 */
    }
    &__cell--aside {
      flex: 0 0 percentage(5/18);
      max-width: percentage(5/18); /* 2. */
    }
    &__cell--aside + &__cell,
    &__cell:first-child:last-child {
      flex: 0 0 percentage(8/18);
      max-width: percentage(8/18); /* 2. */
    }
    &__cell:first-child:last-child {
      margin-left: auto;
      margin-right: auto;
    }
    &__cell--aside + &__cell:last-child {
      flex: 0 1 percentage(13/18);
      max-width: percentage(13/18); /* 2. */
    }
    &__cell &__cell--aside {
      flex: 0 0 percentage(5/13);
      max-width: percentage(5/13); /* 2. */
    }
    &__cell--divider {
      // border-right: 1px solid $color-green;
      // border-color: rgba($color-green, .26);

      /* Rectangle: */
      box-shadow:
        inset (rhythm(-5) + 1px) 0px 0px 0px #FFFFFF,
        inset rhythm(-5) 0px 0px 0px rgba($color-green, .26);

      .well &,
      &.well {
        // border-color: rgba(255,255,255, .70);
        box-shadow:
          inset (rhythm(-5) + 1px) 0px 0px mix($color-theme, #FFF, 26%),
          inset rhythm(-5) 0px 0px 0px rgba(#FFFFFF, .7);

      }
    }
  }
}

@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
  .layout {
    display: inline-block;
    max-width: 100%;
  }
  .layout__cell {
    display: inline-block;
    vertical-align: top;
    float: left;
  }
  @include breakpoint($gt-large-handheld) {
    .layout {
      &__cell:first-child:last-child {
        margin-left: 15%;
        margin-right: 15%;
        width: 100%;
        max-width: 70%;
      }
    }
  }
}

.gutter {
	padding-right: gutter(1);
	&--left {
		padding-left: gutter();
	}
	&--right {
		padding-right: gutter();
	}
	&s {
		padding-left: gutter();
		padding-right: gutter();
	}
}

.gutter {
	&-outside {
		margin-right: gutter(1);
	}
	&-outside--left {
		margin-left: gutter();
	}
	&-outside--right {
		margin-left: gutter();
	}
	&s-outside {
		margin-left: gutter();
		margin-right: gutter();
	}
}

@each $breakpoint, $layout in $layouts {

	$_gw: $gutter-width;

	// Check if the gutter-width has changed since the previous breakpoint
	@if $_gw != get-gutter-width($breakpoint) {

		// Set gutters for this breakpoint
		@include breakpoint( $breakpoint ) {
			.gutter {
				&--left {
					padding-left: gutter($breakpoint);
				}
				&--right {
					padding-left: gutter($breakpoint);
				}
				&s {
					padding-left: gutter($breakpoint);
					padding-right: gutter($breakpoint);
				}
				&-outside--left {
					margin-left: gutter($breakpoint);
				}
				&-outside--right {
					margin-left: gutter($breakpoint);
				}
				&s-outside {
					margin-left: gutter($breakpoint);
					margin-right: gutter($breakpoint);
				}
			}
		}
	}
}

.wing {
	&s,
	&--left {
		// padding-left: percentage(1/12);
		padding-left: $gutter-width;
	}
	&s,
	&--right {
		// padding-right: percentage(1/12);
		padding-right: $gutter-width;
	}
	// @include breakpoint($landscape) {
	//
	// 		&s,
	// 		&--left {
	// 			padding-left: percentage(1/24);
	// 		}
	// 		&s,
	// 		&--right {
	// 			padding-right: percentage(1/24);
	// 		}
	//
	// }
	@include breakpoint($gt_small_desktop) {
		&s,
		&--left {
			padding-left: percentage(1/12);
		}
		&s,
		&--right {
			padding-right: percentage(1/12);
		}
  }
}

