.navigation {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1;
  padding: rhythm() percentage(1/12);
  background: rgba($white,.6);
  img {
    display: block; /* Hack - isolate in image class */
  }
  &__home {
    height: rhythm(8);
  }
  &__section-marker {
    line-height: rhythm(8);
    margin-left: 1ex;
  }
  .menu {
    text-align: right;
  }
  @include breakpoint($gt-large-handheld) {
    right: percentage(12/24);
  }
  @include breakpoint($gt-small-desktop) {
    padding: rhythm(4) percentage(1/24);
  }
  @include breakpoint($gt-medium-desktop) {
    padding: rhythm(4) percentage(1/36);
    right: percentage(24/36);
  }
}

/*
 * 1. Make sure to cover up introduction on desktop
 */
.p-landingpage {
  .panel {
    position: relative;
  }
  .handshake,
  .invitation,
  .tools {
    min-height: 100%;
    min-height: 100vh;
    .well {
      float: left;
      width: 100%;
      min-height: 50%;
      min-height: 50vh;
    }
  }
  .invitation {
    .shops {
      line-height: 1;
    }
  }
  .handshake,
  .invitation,
  .tools {
    .well {
      padding-left: percentage(1/12);
      padding-right: percentage(1/12);
    }
  }
  .shops,
  .offices {
    padding: 0 !important;
  }
  .shops {
    .title--meta {
      font-size: to-unit(14px, 1em, 12px);
      line-height: to-unit(rhythm(4), 1, 14px);
    }
  }
  .offices {
    text-align: center;
  }
  .tools {
    .well {
      min-height: 0;
    }
    &__header {
      .well__title {
        margin-bottom: 0;
      }
    }
  }
  @include breakpoint($landscape) {
    .handshake,
    .invitation {
      .well {
        width: 50%;
        min-height: 100%;
        min-height: 100vh;
      }
    }
    .tools {
      clear: both;
      width: 100%;
      min-height: 100%;
      min-height: 100vh;
    }
    .handshake .well {
      // background: rgba($black,.3);
      // color: $color-body;
    }
    .handshake,
    .invitation,
    .tools {
      .well {
        // padding-left: percentage(1/12);
        // padding-right: percentage(1/12);
      }
    }
    .offices,
    .shops {
      .grid__cell.tile {
        min-height: 50%;
        min-height: 50vh;
        max-height: 50%;
        max-height: 50vh;
      }
    }
  }
  @include breakpoint($gt-large-handheld,$landscape) {

  }
  @include breakpoint($gt-large-handheld,$portrait) {
    .handshake,
    .invitation {
      min-height: 50%;
      min-height: 50vh;
      .well {
        width: 50%;
        min-height: 100%;
        min-height: 50vh;
      }
    }
    .handshake {
      .well {
        min-height: 50%;
        min-height: 25vh;
        clear: both;
      }
    }
    .handshake,
    .invitation,
    .tools {
      .well {
        // padding-left: percentage(1/12);
        // padding-right: percentage(1/12);
      }
    }
    .offices {
      .brand {
        margin: px-to-pt(rhythm(3)) 0;
      }
    }
  }
  @include breakpoint($gt_small_desktop) {
    .offices,
    .shops {
      .grid__cell.tile {
        min-height: 50%;
        min-height: 25vh;
        max-height: 50%;
        max-height: 25vh;
      }
    }
  }
  @include breakpoint($gt_small_desktop,$landscape) {
    .handshake,
    .invitation {
      float: left;
      width: 50%;
      height: 100%;
      height: 100vh;
      .well {
        width: 100%;
        min-height: 50%;
        min-height: 50vh;
      }
    }
  }
  @include breakpoint($gt_medium_desktop,$landscape) {
    .handshake,
    .invitation {
      width: percentage(24/36);
    }

    .handshake,
    .invitation,
    .tools {
      .well {
        padding-left: percentage(1/24);
        padding-right: percentage(1/24);
      }
    }
    .handshake,
    .invitation {
      min-height: 50%;
      min-height: 50vh;
      height: 50%;
      height: 50vh;
    }
    .handshake {
      .well {
        width: 50%;
        min-height: 50%;
        min-height: 25vh;
        clear: both;
      }
    }
    .invitation {
      .well {
        width: 50%;
        min-height: 100%;
        min-height: 100vh;
      }
    }
    .tools {
      background-color: #FFF; /* 1 */
      position: relative;
      z-index: 1;
    }
    .offices {
      font-size: 1.2em;
    }
    .tools {
      .well {
        min-height: 100%;
        min-height: 100vh;
        height: 100%;
        height: 100vh;
        width: 50%;
        overflow-y: scroll;
      }
      &__header,
      &__body {
      }
      &__header {
        color: $white;
        position: relative;
        &:before {
          display: block;
          content: "";
          position: absolute;
          top: 0; left: 0;
          bottom: 0; right: gutter();
          background: $color-indigo url("/images/content/tools--desktop.jpg") no-repeat center;
          background-size: cover;
        }
        .well__title {
          position: relative;
          top: 1;
        }
      }
      &__body {
        padding: 0 !important;
      }
    }
  }
}
