sections.css:
/* 1.2 Fonts */
@import "base/fonts";


/* 1.3 Settings */
@import "base/settings";


/* 1.1 Settings */
@import "base/colour";


/* 1.4 Base styles */
@import "base/base";

$color-yellow: #F39200;
$color-indigo: #505263;
$color-coral: #E94E2C;
$color-gold: #E3A129;
$color-vanilla: #F7ECB9;
$color-dark-blue: #37383C;
$color-green: #63B452;

$color-rss: #FF6600;
$color-twitter: #55ACEE;
$color-facebook: #3B5998;
$color-linkedin: #0077B5;
$color-youtube: #e52d27;
$color-youtube--dark: #b31217;

$color-background: $white;
$color-body: $color-blue;
$color-theme: $color-teal;
$color-primary: $color-coral;
$color-secondary: $color-yellow;

/* Mixing colours */
$color-highlight: #FFF;
$color-shadow: #000;

/* Button colours */
$color-button: $color-blue;

/* Links colours */
$color-anchor: $color-teal;
$color-anchor--hover: $color-indigo;
$color-anchor--active: $color-dark-blue;
$color-anchor--visited: $color-dark-blue;

/* 1.3.1 Rhythm */
@import "settings/rhythm";

/* 1.3.2 Typography */
@import "settings/typography";

/* 1.3.4 Base ? */
@import "settings/base";

/* 1.3.5 Breakpoint */
@import "settings/breakpoint";

/* 1.3.7 Grid ? */
@import "settings/grid";

/* 1.3.8 Layout */
@import "settings/layout";

/* 1.3.9 Miscellaneaous */
@import "settings/miscellaneous";

$use-brand-type: false;

$font-family: "Lora", "Book Antiqua", "Palatino Linotype", Palatino, serif;
$font-variant: normal;
$font-style: normal;
$font-weight: 300;

$font-family--meta: Arial, Helvetica, sans-serif;
$font-weight--meta: 300;

$font-family--heading: Kanit, Arial, Helvetica, Gadget, sans-serif;
$font-weight--heading: 600;

$lt-small-handheld: "(max-width: #{to-unit(319px,$unit,$font-size)})";
$gt-small-handheld: "(min-width: #{to-unit(320px,$unit,$font-size)})";

$lt-medium-handheld: "(max-width: #{to-unit(519px,$unit,$font-size)})";
$gt-medium-handheld: "(min-width: #{to-unit(520px,$unit,$font-size)})";

$lt-large-handheld: "(max-width: #{to-unit(767px,$unit,$font-size)})";
$gt-large-handheld: "(min-width: #{to-unit(768px,$unit,$font-size)})";

$lt-small-desktop: "(max-width: #{to-unit(1023px,$unit,$font-size)})";
$gt-small-desktop: "(min-width: #{to-unit(1024px,$unit,$font-size)})";

$lt-medium-desktop: "(max-width: #{to-unit(1439px,$unit,$font-size)})";
$gt-medium-desktop: "(min-width: #{to-unit(1440px,$unit,$font-size)})";

$lt-large-desktop: "(max-width: #{to-unit(1599px,$unit,$font-size)})";
$gt-large-desktop: "(min-width: #{to-unit(1600px,$unit,$font-size)})";

$msie: "(-ms-high-contrast: active) and (-ms-high-contrast: none)";

$portrait: "(orientation: portrait)";
$landscape: "(orientation: landscape)";

// $high-res: "(-webkit-min-device-pixel-ratio: 1.5), (min-resolution: 144dpi)";
// $retina: "(-webkit-min-device-pixel-ratio: 2), (min-resolution: 196pi)";

$layouts: (
	$lt-medium-handheld: (
		default: true,
		columns: 12,
		gutter-width: rhythm(7),
		rhythm: rhythm(),
		debug-color: #FFA500, // Orange
		grids: (
			split: (
				main: ( columns: 6, wings: (0,0) ),
				aside: ( columns: 6, wings: (0,0) )
			),
			sidebar: (
				main: ( columns: 6, wings: (0,0) ),
				aside: ( columns: 6, wings: (0,0) )
			)
		),
	),
	$gt-large-handheld: (
		default: false,
		columns: 24,
		gutter-width: rhythm(7),
		rhythm: rhythm(),
		debug-color: #00FFFF, //Cyan
		grids: (
			split: (
				main: ( columns: 14, wings: (2,2) ),
				aside: ( columns: 14, wings: (2,2) )
			),
			tsplit: (
				main: ( columns: 17, wings: (2,0) ),
				aside: ( columns: 9, wings: (2,2) )
			)
		)
	),

	$gt-medium-desktop: (
		default: false,
		columns: 36,
		gutter-width: rhythm(7),
		rhythm: rhythm(),
		debug-color: #00FFFF, //Cyan
		grids: (
			split: (
				main: ( columns: 14, wings: (2,2) ),
				aside: ( columns: 14, wings: (2,2) )
			),
			tsplit: (
				main: ( columns: 17, wings: (2,0) ),
				aside: ( columns: 9, wings: (2,2) )
			)
		)
	)
);

// Get the defaults from the layouts map
$columns: get-columns();
$gutter-width: get-gutter-width();
$rhythm: get-rhythm();

//*! normalize.css v3.0.1 | MIT License | git.io/normalize *//


/*
 * 1. Apply a natural box layout model to all elements
 * 2. Remove margins from all elements
 */

* {
  &,
  &::before,
  &::after {
    box-sizing: border-box;
    margin: 0;
  }
}


/*
 * 1. Remove default margin.
 * 2. Set height to accommodate hero module
 */

html, body
{
    margin: 0; /* 1 */
    // padding: 0; /* 1 */
    height: 100%; /* 2 */
    min-height: 100%; /* 2 */
}

/*
 * 1. Set base font (family, size, line-height) for conversions to rem and/or em.
 * 2. Set default font family to sans-serif.
 * 3. Prevent iOS text size adjust after orientation change, without disabling
 *    user zoom.
 * 4. Set background color.
 * 5. Fallback for browsers that do not support opacity
 */
html {

  width: 100%; /* 3 */
  background: $color-background; /* 4 */
  font: #{$font-style} #{$font-variant} #{$font-weight} #{$font-size}/#{$line-height} #{$font-family};
  color: $color-body; /* 5 */
  text-size-adjust: 100%; /* 3 */
}

/* HTML5 display definitions
   ========================================================================== */

/*
 * Correct `block` display not defined for any HTML5 element in IE 8/9.
 * Correct `block` display not defined for `details` or `summary` in IE 10/11 and Firefox.
 * Correct `block` display not defined for `main` in IE 11.
 */

article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
main,
nav,
section,
summary {
  display: block;
}

/*
 * 1. Correct `inline-block` display not defined in IE 8/9.
 * 2. Normalize vertical alignment of `progress` in Chrome, Firefox, and Opera.
 */

audio,
canvas,
progress,
video {
  display: inline-block; /* 1 */
  vertical-align: baseline; /* 2 */
}

/*
 * Prevent modern browsers from displaying `audio` without controls.
 * Remove excess height in iOS 5 devices.
 */

audio:not([controls]) {
  display: none;
  height: 0;
}

/*
 * Address `[hidden]` styling not present in IE 8/9/10.
 * Hide the `template` element in IE 8/9/11, Safari, and Firefox < 22.
 */

[hidden],
template {
  display: none;
}

/* Links
   ========================================================================== */

/*
 * 1. Remove the gray background color from active links in IE 10.
 * 2. Set default anchor colour.
 */

a {
  background: transparent;
  color: $color-anchor;
}

/*
 * Improve readability when focused and also mouse hovered in all browsers.
 */

a:active,
a:hover {
  outline: 0;
}

/* Text-level semantics
   ========================================================================== */

/*
 * Address styling not present in IE 8/9/10/11, Safari, and Chrome.
 */

abbr[title] {
  border-bottom: 1px dotted;
}

/*
 * Address style set to `bolder` in Firefox 4+, Safari, and Chrome.
 */

b,
strong {
  font-weight: bold;
}

/*
 * Address styling not present in Safari and Chrome.
 */

dfn {
  font-style: italic;
}

/*
 * Reset the headers – their visual appearance is decoupled from their semantic meaning
 */

h1 {
  font-size: inherit;
  line-height: inherit;
  margin: 0;
}

/*
 * Address styling not present in IE 8/9.
 */

mark {
  background: $color-yellow;
  color: $color-body;
}

/*
 * Address inconsistent and variable font size in all browsers.
 */

small {
    font-size: to-unit(10px, 1em);
}

/*
 * Prevent `sub` and `sup` affecting `line-height` in all browsers.
 */

sub,
sup {
  font-size: 75%;
  line-height: 0;
  position: relative;
  vertical-align: baseline;
}

sup {
  top: -0.5em;
}

sub {
  bottom: -0.25em;
}

/* Embedded content
   ========================================================================== */

/*
 * Remove border when inside `a` element in IE 8/9/10.
 */

img {
  border: 0;
}

/*
 * Correct overflow not hidden in IE 9/10/11.
 */

svg:not(:root) {
  overflow: hidden;
}

/* Grouping content
   ========================================================================== */

/*
 * Reset margin
 */

figure {
  margin: 0;
}

/*
 * Address differences between Firefox and other browsers.
 */

hr {
  box-sizing: content-box;
  height: 0;
}

/*
 * Contain overflow in all browsers.
 */

pre {
  overflow: auto;
}

/*
 * Reset address and em styling.
 */

address,
em {
  font-style: inherit;
}

/*
 * Address odd `em`-unit font size rendering in all browsers.
 */

code,
kbd,
pre,
samp {
  font-family: monospace;
  font-size: 1em;
}

/* Forms
   ========================================================================== */

/*
 * Known limitation: by default, Chrome and Safari on OS X allow very limited
 * styling of `select`, unless a `border` property is set.
 */

/*
 * 1. Correct color not being inherited.
 *    Known issue: affects color of disabled elements.
 * 2. Correct font properties not being inherited.
 * 3. Address margins set differently in Firefox 4+, Safari, and Chrome.
 */

button,
input,
optgroup,
select,
textarea {
  color: inherit; /* 1 */
  font: inherit; /* 2 */
  margin: 0; /* 3 */
}

/*
 * Address `overflow` set to `hidden` in IE 8/9/10/11.
 */

button {
  overflow: visible;
}

/*
 * Address inconsistent `text-transform` inheritance for `button` and `select`.
 * All other form control elements do not inherit `text-transform` values.
 * Correct `button` style inheritance in Firefox, IE 8/9/10/11, and Opera.
 * Correct `select` style inheritance in Firefox.
 */

button,
select {
  text-transform: none;
}

/*
 * 1. Avoid the WebKit bug in Android 4.0.* where (2) destroys native `audio`
 *    and `video` controls.
 * 2. Correct inability to style clickable `input` types in iOS.
 * 3. Improve usability and consistency of cursor style between image-type
 *    `input` and others.
 */

button,
html input[type="button"], /* 1 */
input[type="reset"],
input[type="submit"] {
  @include reset-appearance(button); /* 2 */
  cursor: pointer; /* 3 */
}

/*
 * Re-set default cursor for disabled elements.
 */

button[disabled],
html input[disabled] {
  cursor: default;
}

/*
 * Remove inner padding and border in Firefox 4+.
 */

button::-moz-focus-inner,
input::-moz-focus-inner {
  border: 0;
  padding: 0;
}

/*
 * Address Firefox 4+ setting `line-height` on `input` using `!important` in
 * the UA stylesheet.
 */

input {
  line-height: normal;
}

/*
 * It's recommended that you don't attempt to style these elements.
 * Firefox's implementation doesn't respect box-sizing, padding, or width.
 *
 * 1. Address box sizing set to `content-box` in IE 8/9/10.
 * 2. Remove excess padding in IE 8/9/10.
 */

input[type="checkbox"],
input[type="radio"] {
  box-sizing: border-box; /* 1 */
  padding: 0; /* 2 */
}

/*
 * Fix the cursor style for Chrome's increment/decrement buttons. For certain
 * `font-size` values of the `input`, it causes the cursor style of the
 * decrement button to change from `default` to `text`.
 */

input[type="number"]::-webkit-inner-spin-button,
input[type="number"]::-webkit-outer-spin-button {
  height: auto;
}

/*
 * 1. Address `appearance` set to `searchfield` in Safari and Chrome.
 * 2. Address `box-sizing` set to `border-box` in Safari and Chrome
 *    (include `-moz` to future-proof).
 */

input[type="search"] {
  @include reset-appearance(textfield); /* 1 */
  // is already set in wildcard rule /* 2 */
}

/*
 * Remove inner padding and search cancel button in Safari and Chrome on OS X.
 * Safari (but not Chrome) clips the cancel button when the search input has
 * padding (and `textfield` appearance).
 */

input[type="search"]::-webkit-search-cancel-button,
input[type="search"]::-webkit-search-decoration {
  @include reset-appearance(none);
}

/*
 * Define consistent border, margin, and padding.
 */

fieldset {
  border: 0;
  margin: 0 rhythm();
  padding: 0;
  &--reset {
    border: 0;
    margin: 0;
    padding: 0;
  }
}

/*
 * 1. Correct `color` not being inherited in IE 8/9/10/11.
 * 2. Remove padding so people aren't caught out if they zero out fieldsets.
 */

legend {
  border: 0; /* 1 */
  padding: 0; /* 2 */
}

/*
 * Remove default vertical scrollbar in IE 8/9/10/11.
 */

textarea {
  overflow: auto;
}

/*
 * Don't inherit the `font-weight` (applied by a rule above).
 * NOTE: the default cannot safely be changed in Chrome and Safari on OS X.
 */

optgroup {
  font-weight: bold;
}

/* Tables
   ========================================================================== */

/*
 * Remove most spacing between table cells.
 */

table {
  border-collapse: collapse;
  border-spacing: 0;
}

td,
th {
  padding: 0;
}

/*
-----------------------------------------------------------------------------
Miscellaneous
-----------------------------------------------------------------------------
*/

p {
  margin: 0;
}

abbr[title] {
    border-bottom: 1px dotted;
}
hr {
    height: 0;
}
code,
kbd,
pre,
samp {
    @include font-family($monospace-regular);
    @include font-size($font-size);
}
pre {
    white-space: pre-wrap;
}
sub,
sup {
    font-size: 75%;
    line-height: 0;
    position: relative;
    vertical-align: baseline;
}
sup {
    top: -0.5em;
}
sub {
    bottom: -0.25em;
}

/*
 * Set consistent quote types.
 */
q {
    quotes: "\201C" "\201D" "\2018" "\2019";
    &--reset {
      quotes: "";
    }
}

