/*
 * Reset list
 */
fieldset,
ol, ul, dl {
  &.reset {
    margin: 0;
    padding: 0;
  }
}
ol, ul {
  &.reset {
    list-style: none;
  }
}

h1, h2, h3, h4, h5, h6 {
  font-weight: 300;
  font-size: 1em;
}

p,
fieldset,
figure {
  &.reset {
    margin: 0;
  }
}
fieldset {
  &.reset {
    border: 0;
  }
}

button,
input {
    &.reset {
      background: none;
      border: 0;
      padding: 0;
    }
}

label {
  display: inline-block;
}

label,
input,
select,
textarea {
  width: 100%;
}
input[type="checkbox"]
input[type="radio"] {
  width: initial;
}

$debug-layout: true;
$debug-rhythm: true;
$debug-breakpoint: true;

@if $debug-breakpoint {
  html:after,
  body:after {
    display: block;
    position: fixed;
    right: 0;
    background: #fff;
    padding: rhythm();
  }
  html:after {
    top: 0;
    @include breakpoint($gt-small-handheld,$landscape) {
      content: 'gt-small-handheld - landscape';
    }
    @include breakpoint($gt-small-handheld,$portrait) {
      content: 'gt-small-handheld - portrait';
    }
    @include breakpoint($gt-medium-handheld,$landscape) {
      content: 'gt-medium-handheld - landscape';
    }
    @include breakpoint($gt-medium-handheld,$portrait) {
      content: 'gt-medium-handheld - portrait';
    }
    @include breakpoint($gt-large-handheld,$landscape) {
      content: 'gt-large-handheld - landscape';
    }
    @include breakpoint($gt-large-handheld,$portrait) {
      content: 'gt-large-handheld - portrait';
    }
    @include breakpoint($gt-small-desktop,$landscape) {
      content: 'gt-small-desktop - landscape';
    }
    @include breakpoint($gt-small-desktop,$portrait) {
      content: 'gt-small-desktop - portrait';
    }
    @include breakpoint($gt-medium-desktop,$landscape) {
      content: 'gt-medium-desktop - landscape';
    }
    @include breakpoint($gt-medium-desktop,$portrait) {
      content: 'gt-medium-desktop - portrait';
    }
    @include breakpoint($gt-large-desktop,$landscape) {
      content: 'gt-large-desktop - landscape';
    }
    @include breakpoint($gt-large-desktop,$portrait) {
      content: 'gt-large-desktop - portrait';
    }
  }
}

@mixin debug-rhythm($rhythm: $rhythm)
{
    &:after {

        $to: transparent;
        $from: rgba(226, 255, 255, .25);

        pointer-events: none;
        display: block;
        position: absolute;
        left: 0; top: 0; right: 0; bottom: 0;
        z-index: 10;
        content: "";
        background-image: -webkit-linear-gradient($from 50%, $to 50%);
        background-image: linear-gradient($from 50%, $to 50%);
        background-size: 2*$rhythm 2*$rhythm;
    }
}

@mixin debug-layout($columns: $columns, $color: #000, $gutter-width: $gutter-width)
{
    background-size: percentage(1 / $columns) 100%;
    background-image:
        linear-gradient(to right,
            rgba($color,.1) 0,
            rgba($color,.1) $gutter-width / 2,
            transparent $gutter-width / 2,
            transparent percentage($columns / $columns)
        ),
        linear-gradient(to left,
            rgba($color,.1) 0,
            rgba($color,.1) $gutter-width / 2,
            transparent $gutter-width / 2,
            transparent percentage($columns / $columns)
        ),
        linear-gradient(to left,
            transparent 0%,
            rgba(0,0,0,.5) 1px,
            transparent 1px,
            transparent  percentage($columns / $columns)
        )
    ;
}

@if $debug-layout {
    %debug-layout {
        position: relative;
        &::before {
        	pointer-events: none;
            content: '';
            display: block;
            position: absolute;
            z-index: 10;
            left: 0;
            top: 0;
            bottom: 0;
            right: 0;

            background-color: transparent;
            background-repeat: repeat-x;
            background-position: 0 0;

            @include debug-layout();

            @each $breakpoint, $layout in $layouts {
            	@include breakpoint($breakpoint) {
                    $columns: map-get($layout, "columns");
                    $color: map-get($layout, "debug-color");
                    @include debug-layout($columns, $color, $gutter-width);
		                content: '#{$columns} columns at #{$breakpoint}';
                    text-shadow: 1px 1px 0 $white;
                }
            }
        }
}

    .debug-layout {
        @extend %debug-layout;
    }
    .debug-rhythm {
        @include debug-rhythm();
    }
}



/* 2.1 Layout */
@import "layout/layout";

/* 2.2 Gutter */
@import "layout/gutter";

/* 2.3 Wing */
@import "layout/wing";

/* 2.4 Leader */
@import "layout/leader";

/* 2.5 Trailer */
@import "layout/trailer";

/* 2.6 Bleed */
@import "layout/bleed";

/* 2.7 Grid */
@import "layout/grid";

/* 2.8 Media */
@import "layout/media";

/* 2.9 Tier */
@import "layout/tier";

/* 2.10 Align */
@import "layout/align";

.gutter {
	padding-right: gutter(1);
	&--left {
		padding-left: gutter();
	}
	&--right {
		padding-right: gutter();
	}
	&s {
		padding-left: gutter();
		padding-right: gutter();
	}
}

.gutter {
	&-outside {
		margin-right: gutter(1);
	}
	&-outside--left {
		margin-left: gutter();
	}
	&-outside--right {
		margin-left: gutter();
	}
	&s-outside {
		margin-left: gutter();
		margin-right: gutter();
	}
}

@each $breakpoint, $layout in $layouts {

	$_gw: $gutter-width;

	// Check if the gutter-width has changed since the previous breakpoint
	@if $_gw != get-gutter-width($breakpoint) {

		// Set gutters for this breakpoint
		@include breakpoint( $breakpoint ) {
			.gutter {
				&--left {
					padding-left: gutter($breakpoint);
				}
				&--right {
					padding-left: gutter($breakpoint);
				}
				&s {
					padding-left: gutter($breakpoint);
					padding-right: gutter($breakpoint);
				}
				&-outside--left {
					margin-left: gutter($breakpoint);
				}
				&-outside--right {
					margin-left: gutter($breakpoint);
				}
				&s-outside {
					margin-left: gutter($breakpoint);
					margin-right: gutter($breakpoint);
				}
			}
		}
	}
}

.wing {
	&s,
	&--left {
		padding-left: percentage(1/12);
		// padding-left: $gutter-width;
	}
	&s,
	&--right {
		padding-right: percentage(1/12);
		// padding-right: $gutter-width;
	}
	// @include breakpoint($landscape) {
	//
	// 		&s,
	// 		&--left {
	// 			padding-left: percentage(1/24);
	// 		}
	// 		&s,
	// 		&--right {
	// 			padding-right: percentage(1/24);
	// 		}
	//
	// }
	@include breakpoint($gt_small_desktop) {
		&s,
		&--left {
			padding-left: percentage(1/12);
		}
		&s,
		&--right {
			padding-right: percentage(1/12);
		}
  }
}

.leader {
	margin-top: px-to-pt(rhythm(4));
	&--tiny {
		margin-top: px-to-pt(rhythm(2));
	}
	&--hero {
		margin-top: px-to-pt(rhythm(8));
	}
	&--giant {
		margin-top: px-to-pt(rhythm(10));
	}
	@include breakpoint($gt-large-handheld) {
			margin-top: px-to-pt(rhythm(5));
  }
	@include breakpoint($gt-small-desktop) {
		&--giant {
			margin-top: px-to-pt(rhythm(18));
		}
	}
}

.leader-inside {
	padding-top: px-to-pt(rhythm(4));
	&--tiny {
		padding-top: px-to-pt(rhythm(2));
	}
	&--hero {
		padding-top: px-to-pt(rhythm(8));
	}
	&--giant {
		padding-top: px-to-pt(rhythm(16));
	}
	@include breakpoint($gt-large-handheld) {
			padding-top: px-to-pt(rhythm(5));
  }
	@include breakpoint($gt-small-desktop) {
		&--giant {
			padding-top: px-to-pt(rhythm(30));
		}
	}
}

.trailer {
	margin-bottom: px-to-pt(rhythm(4));
	&--tiny {
		margin-bottom: px-to-pt(rhythm(2));
	}
	&--hero {
		margin-bottom: px-to-pt(rhythm(8));
	}
	&--giant {
		margin-bottom: px-to-pt(rhythm(10));
	}
	@include breakpoint($gt-large-handheld) {
			margin-bottom: px-to-pt(rhythm(5));
  }
	@include breakpoint($gt-small-desktop) {
		&--giant {
			margin-bottom: px-to-pt(rhythm(18));
		}
	}
}

.trailer-inside {
	padding-bottom: px-to-pt(rhythm(4));
