.grid {
  display: flex;
  flex-wrap: wrap;
  // flex-direction: row;
  // height: 100%;
  &:after {
    /* 1. */
    content:" ";
    display:block;
    clear:both;
  }
  &__cell {
    float: left;
    width: 50%;
    height: 50%;
    height: 50vh;
  }
  /* Need breakpoint for wide and narrow viewports */
  @include breakpoint($gt-small-desktop,"(max-aspect-ratio: 2/1)") {
    &__cell {
      height: 25vh;
    }
  }
}

.media {
  display: flex;
  &__image {

  }
  &__content {

  }
  &__stretcher {
    flex-grow: 1;
  }
  &--middle {
    align-items: center;
  }
}

// Consider replacing with no-flex fallback via Modernizr
@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
  .media {
    display: inline-block;
    >* {
      display: inline-block;
    }
    .image {
      display: inline-block;
    }

  }
}

.tier {
	flex-grow: 1;
	&--align-bottom {
		flex-grow: 0;
	}
	&-group {
		height: 100%;
		display: flex;
		flex-direction: column;
	}
}

.align {
	&--center,
	&--middle {
		position: relative;
		top: 50%;
	}
	&--center {
		left: 50%;
		transform: translateY(-50%) translateX(-50%);
	}
	&--middle {
		transform: translateY(50%);
	}
}

