      background: $white;
      background: rgba($white, .8);
    }
    &--hippo &__seed {
      padding: 0 percentage(2/36) 0 percentage(4/36);
    }
  }
  @include breakpoint($gt-medium-desktop) {
    &__seed {
      padding: 0 percentage(2/36);
    }
    &--rhino {
      width: percentage(24/36);
      max-width: percentage(24/36);
    }
    &__serval {
      position: relative;
    }
    &--baboon {
      width: percentage(12/36);
    }
    &--leopard {
      width: percentage(12/36);
    }
  }
}

/*
 * 1. Layout
 * 2. Background image
 */
.tile {
  position: relative;
  // display: table-cell; /* 1 */
  // vertical-align: middle; /* 1 */
  // box-shadow: inset 1px 1px 0 $white, inset -1px -1px 0 $white;
  &__body {
    // width: 100%;
    position: absolute; /* 1 */
    top: 50%; /* 1 */
    left: 0;
    right: 0;
    transform: translateY(-50%); /* 1 */
    // padding: inherit;
  }
  &--shop &__body {
    text-align: center; /* 1 */
  }
  &--top {
    vertical-align: top;
  }
  &--gutters {
    @include gutters;
  }
  &__header,
  &__footer {
    // position: absolute;
    // left: 0;
    // right: 0;
  }
  &__header {
    // top: 0;
  }
  &__footer {
    // bottom: 0;
  }
  &--gutters &__header,
  &--gutters &__footer {
    left: .5 * $gutter-width;
    right: .5 * $gutter-width;
  }

}

.person {
  position: relative;
  // left: px-to-pt(-1*rhythm(5));
  display: table;
  max-width: 380px;
  &__picture,
  &__body {
    display: table-cell;
    vertical-align: middle;
    line-height: px-to-pt(rhythm(3));
  }
  &__image {
    outline: 1px solid;
    width: px-to-pt(rhythm(10));
    height: px-to-pt(rhythm(10));
  }
  &--s &__image {
      width: px-to-pt(rhythm(8));
      height: px-to-pt(rhythm(8));
  }
  @include breakpoint($gt-small-desktop) {
    // left: px-to-pt(-1*rhythm(6));
    &__image {
      width: px-to-pt(rhythm(12));
      height: px-to-pt(rhythm(12));
    }
  }
}

.trail {
  font-family: Arial, "Helvetica Neue", Helvetica, sans-serif;
  text-transform: uppercase;
  color: $color-blue;
  li {
    display: block;
    float: left;
    padding-right: rhythm(4);
  }
  li + li {
    &:before {
      content: "|";
      padding-right: rhythm(4);
    }
  }
}

