section.css:
/* 1.2 Fonts */
@import "base/fonts";


/* 1.3 Settings */
@import "base/settings";


/* 1.1 Settings */
@import "base/colour";


/* 1.4 Base styles */
@import "base/base";

$color-yellow: #F39200;
$color-indigo: #505263;
$color-coral: #E94E2C;
$color-gold: #E3A129;
$color-vanilla: #F7ECB9;
$color-dark-blue: #37383C;
$color-green: #63B452;

$color-rss: #FF6600;
$color-twitter: #55ACEE;
$color-facebook: #3B5998;
$color-linkedin: #0077B5;
$color-youtube: #e52d27;
$color-youtube--dark: #b31217;

$color-background: $white;
$color-body: $color-blue;
$color-theme: $color-teal;
$color-primary: $color-coral;
$color-secondary: $color-yellow;

/* Mixing colours */
$color-highlight: #FFF;
$color-shadow: #000;

/* Button colours */
$color-button: $color-blue;

/* Links colours */
$color-anchor: $color-teal;
$color-anchor--hover: $color-indigo;
$color-anchor--active: $color-dark-blue;
$color-anchor--visited: $color-dark-blue;

/* 1.3.1 Rhythm */
@import "settings/rhythm";

/* 1.3.2 Typography */
@import "settings/typography";

/* 1.3.4 Base ? */
@import "settings/base";

/* 1.3.5 Breakpoint */
@import "settings/breakpoint";

/* 1.3.7 Grid ? */
@import "settings/grid";

/* 1.3.8 Layout */
@import "settings/layout";

/* 1.3.9 Miscellaneaous */
@import "settings/miscellaneous";

$use-brand-type: false;

$font-family: "Lora", "Book Antiqua", "Palatino Linotype", Palatino, serif;
$font-variant: normal;
$font-style: normal;
$font-weight: 300;

$font-family--meta: Arial, Helvetica, sans-serif;
$font-weight--meta: 300;

$font-family--heading: Kanit, Arial, Helvetica, Gadget, sans-serif;
$font-weight--heading: 600;

$lt-small-handheld: "(max-width: #{to-unit(319px,$unit,$font-size)})";
$gt-small-handheld: "(min-width: #{to-unit(320px,$unit,$font-size)})";

$lt-medium-handheld: "(max-width: #{to-unit(519px,$unit,$font-size)})";
$gt-medium-handheld: "(min-width: #{to-unit(520px,$unit,$font-size)})";

$lt-large-handheld: "(max-width: #{to-unit(767px,$unit,$font-size)})";
$gt-large-handheld: "(min-width: #{to-unit(768px,$unit,$font-size)})";

$lt-small-desktop: "(max-width: #{to-unit(1023px,$unit,$font-size)})";
$gt-small-desktop: "(min-width: #{to-unit(1024px,$unit,$font-size)})";

$lt-medium-desktop: "(max-width: #{to-unit(1439px,$unit,$font-size)})";
$gt-medium-desktop: "(min-width: #{to-unit(1440px,$unit,$font-size)})";

$lt-large-desktop: "(max-width: #{to-unit(1599px,$unit,$font-size)})";
$gt-large-desktop: "(min-width: #{to-unit(1600px,$unit,$font-size)})";

$msie: "(-ms-high-contrast: active) and (-ms-high-contrast: none)";

$portrait: "(orientation: portrait)";
$landscape: "(orientation: landscape)";

// $high-res: "(-webkit-min-device-pixel-ratio: 1.5), (min-resolution: 144dpi)";
// $retina: "(-webkit-min-device-pixel-ratio: 2), (min-resolution: 196pi)";

$layouts: (
	$lt-medium-handheld: (
		default: true,
		columns: 12,
		gutter-width: rhythm(7),
		rhythm: rhythm(),
		debug-color: #FFA500, // Orange
		grids: (
			split: (
				main: ( columns: 6, wings: (0,0) ),
				aside: ( columns: 6, wings: (0,0) )
			),
			sidebar: (
				main: ( columns: 6, wings: (0,0) ),
				aside: ( columns: 6, wings: (0,0) )
			)
		),
	),
	$gt-large-handheld: (
		default: false,
		columns: 24,
		gutter-width: rhythm(7),
		rhythm: rhythm(),
		debug-color: #00FFFF, //Cyan
		grids: (
			split: (
				main: ( columns: 14, wings: (2,2) ),
				aside: ( columns: 14, wings: (2,2) )
			),
			tsplit: (
				main: ( columns: 17, wings: (2,0) ),
				aside: ( columns: 9, wings: (2,2) )
			)
		)
	),

	$gt-medium-desktop: (
		default: false,
		columns: 36,
		gutter-width: rhythm(7),
		rhythm: rhythm(),
		debug-color: #00FFFF, //Cyan
		grids: (
			split: (
				main: ( columns: 14, wings: (2,2) ),
				aside: ( columns: 14, wings: (2,2) )
			),
			tsplit: (
				main: ( columns: 17, wings: (2,0) ),
				aside: ( columns: 9, wings: (2,2) )
			)
		)
	)
);

// Get the defaults from the layouts map
$columns: get-columns();
$gutter-width: get-gutter-width();
$rhythm: get-rhythm();

//*! normalize.css v3.0.1 | MIT License | git.io/normalize *//


/*
 * 1. Apply a natural box layout model to all elements
 * 2. Remove margins from all elements
 */

* {
  &,
  &::before,
  &::after {
    box-sizing: border-box;
    margin: 0;
  }
}


/*
 * 1. Remove default margin.
 * 2. Set height to accommodate hero module
 */

html, body
{
    margin: 0; /* 1 */
    // padding: 0; /* 1 */
    height: 100%; /* 2 */
    min-height: 100%; /* 2 */
}

/*
 * 1. Set base font (family, size, line-height) for conversions to rem and/or em.
 * 2. Set default font family to sans-serif.
 * 3. Prevent iOS text size adjust after orientation change, without disabling
 *    user zoom.
 * 4. Set background color.
 * 5. Fallback for browsers that do not support opacity
 */
html {

  width: 100%; /* 3 */
  background: $color-background; /* 4 */
  font: #{$font-style} #{$font-variant} #{$font-weight} #{$font-size}/#{$line-height} #{$font-family};
  color: $color-body; /* 5 */
  text-size-adjust: 100%; /* 3 */
}

/* HTML5 display definitions
   ========================================================================== */

/*
 * Correct `block` display not defined for any HTML5 element in IE 8/9.
 * Correct `block` display not defined for `details` or `summary` in IE 10/11 and Firefox.
 * Correct `block` display not defined for `main` in IE 11.
 */

article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
main,
nav,
section,
summary {
  display: block;
}

/*
 * 1. Correct `inline-block` display not defined in IE 8/9.
 * 2. Normalize vertical alignment of `progress` in Chrome, Firefox, and Opera.
 */

audio,
canvas,
progress,
video {
  display: inline-block; /* 1 */
  vertical-align: baseline; /* 2 */
}

/*
 * Prevent modern browsers from displaying `audio` without controls.
 * Remove excess height in iOS 5 devices.
 */

audio:not([controls]) {
  display: none;
  height: 0;
}

/*
 * Address `[hidden]` styling not present in IE 8/9/10.
 * Hide the `template` element in IE 8/9/11, Safari, and Firefox < 22.
 */

[hidden],
template {
  display: none;
}

/* Links
   ========================================================================== */

/*
 * 1. Remove the gray background color from active links in IE 10.
 * 2. Set default anchor colour.
 */

a {
  background: transparent;
  color: $color-anchor;
}

/*
 * Improve readability when focused and also mouse hovered in all browsers.
 */

a:active,
a:hover {
  outline: 0;
}

/* Text-level semantics
   ========================================================================== */

/*
 * Address styling not present in IE 8/9/10/11, Safari, and Chrome.
 */

abbr[title] {
  border-bottom: 1px dotted;
}

/*
 * Address style set to `bolder` in Firefox 4+, Safari, and Chrome.
 */

b,
strong {
  font-weight: bold;
}

/*
 * Address styling not present in Safari and Chrome.
 */

dfn {
  font-style: italic;
}

/*
 * Reset the headers – their visual appearance is decoupled from their semantic meaning
 */

h1 {
  font-size: inherit;
  line-height: inherit;
  margin: 0;
}

/*
 * Address styling not present in IE 8/9.
 */

mark {
  background: $color-yellow;
  color: $color-body;
}

/*
 * Address inconsistent and variable font size in all browsers.
 */

small {
    font-size: to-unit(10px, 1em);
}

/*
 * Prevent `sub` and `sup` affecting `line-height` in all browsers.
 */

sub,
sup {
  font-size: 75%;
  line-height: 0;
  position: relative;
