.gutter {
	padding-right: gutter(1);
	&--left {
		padding-left: gutter();
	}
	&--right {
		padding-right: gutter();
	}
	&s {
		padding-left: gutter();
		padding-right: gutter();
	}
}

.gutter {
	&-outside {
		margin-right: gutter(1);
	}
	&-outside--left {
		margin-left: gutter();
	}
	&-outside--right {
		margin-left: gutter();
	}
	&s-outside {
		margin-left: gutter();
		margin-right: gutter();
	}
}

@each $breakpoint, $layout in $layouts {

	$_gw: $gutter-width;

	// Check if the gutter-width has changed since the previous breakpoint
	@if $_gw != get-gutter-width($breakpoint) {

		// Set gutters for this breakpoint
		@include breakpoint( $breakpoint ) {
			.gutter {
				&--left {
					padding-left: gutter($breakpoint);
				}
				&--right {
					padding-left: gutter($breakpoint);
				}
				&s {
					padding-left: gutter($breakpoint);
					padding-right: gutter($breakpoint);
				}
				&-outside--left {
					margin-left: gutter($breakpoint);
				}
				&-outside--right {
					margin-left: gutter($breakpoint);
				}
				&s-outside {
					margin-left: gutter($breakpoint);
					margin-right: gutter($breakpoint);
				}
			}
		}
	}
}

.wing {
	&s,
	&--left {
		padding-left: percentage(1/12);
		// padding-left: $gutter-width;
	}
	&s,
	&--right {
		padding-right: percentage(1/12);
		// padding-right: $gutter-width;
	}
	// @include breakpoint($landscape) {
	//
	// 		&s,
	// 		&--left {
	// 			padding-left: percentage(1/24);
	// 		}
	// 		&s,
	// 		&--right {
	// 			padding-right: percentage(1/24);
	// 		}
	//
	// }
	@include breakpoint($gt_small_desktop) {
		&s,
		&--left {
			padding-left: percentage(1/12);
		}
		&s,
		&--right {
			padding-right: percentage(1/12);
		}
  }
}

.leader {
	margin-top: px-to-pt(rhythm(4));
	&--tiny {
		margin-top: px-to-pt(rhythm(2));
	}
	&--hero {
		margin-top: px-to-pt(rhythm(8));
	}
	&--giant {
		margin-top: px-to-pt(rhythm(10));
	}
	@include breakpoint($gt-large-handheld) {
			margin-top: px-to-pt(rhythm(5));
  }
	@include breakpoint($gt-small-desktop) {
		&--giant {
			margin-top: px-to-pt(rhythm(18));
		}
	}
}

.leader-inside {
	padding-top: px-to-pt(rhythm(4));
	&--tiny {
		padding-top: px-to-pt(rhythm(2));
	}
	&--hero {
		padding-top: px-to-pt(rhythm(8));
	}
	&--giant {
		padding-top: px-to-pt(rhythm(16));
	}
	@include breakpoint($gt-large-handheld) {
			padding-top: px-to-pt(rhythm(5));
  }
	@include breakpoint($gt-small-desktop) {
		&--giant {
			padding-top: px-to-pt(rhythm(30));
		}
	}
}

.trailer {
	margin-bottom: px-to-pt(rhythm(4));
	&--tiny {
		margin-bottom: px-to-pt(rhythm(2));
	}
	&--hero {
		margin-bottom: px-to-pt(rhythm(8));
	}
	&--giant {
		margin-bottom: px-to-pt(rhythm(10));
	}
	@include breakpoint($gt-large-handheld) {
			margin-bottom: px-to-pt(rhythm(5));
  }
	@include breakpoint($gt-small-desktop) {
		&--giant {
			margin-bottom: px-to-pt(rhythm(18));
		}
	}
}

.trailer-inside {
	padding-bottom: px-to-pt(rhythm(4));
	&--tiny {
		padding-bottom: px-to-pt(rhythm(2));
	}
	&--hero {
		padding-bottom: px-to-pt(rhythm(8));
	}
	&--giant {
		padding-bottom: px-to-pt(rhythm(16));
	}
	@include breakpoint($gt-large-handheld) {
			padding-bottom: px-to-pt(rhythm(5));
  }
	@include breakpoint($gt-small-desktop) {
		&--giant {
			padding-bottom: px-to-pt(rhythm(30));
		}
	}
}

.bleed {
	margin-left: gutter(-1/2);
	margin-right: gutter(-1/2);
	&--left {
		margin-left: gutter(-1/2);
	}
	&--right {
		margin-right: gutter(-1/2);
	}
}

/*
 * 1. Clearfix reloaded (http://cssmojo.com/the-very-latest-clearfix-reloaded/)
 */

.grid {
  display: flex;
  flex-wrap: wrap;
  // flex-direction: row;
  // height: 100%;
  &:after {
    /* 1. */
    content:" ";
    display:block;
    clear:both;
  }
  &__cell {
    float: left;
    width: 50%;
    height: 50%;
    height: 50vh;
  }
  /* Need breakpoint for wide and narrow viewports */
  @include breakpoint($gt-small-desktop,"(max-aspect-ratio: 2/1)") {
    &__cell {
      height: 25vh;
    }
  }
}

.media {
  display: flex;
  &__image {

  }
  &__content {

  }
  &__stretcher {
    flex-grow: 1;
  }
  &--middle {
    align-items: center;
  }
}

// Consider replacing with no-flex fallback via Modernizr
@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
  .media {
    display: inline-block;
    >* {
      display: inline-block;
    }
    .image {
      display: inline-block;
    }

  }
}

.tier {
	flex-grow: 1;
	&--align-bottom {
		flex-grow: 0;
	}
	&-group {
		height: 100%;
		display: flex;
		flex-direction: column;
	}
}

.align {
	&--center,
	&--middle {
		position: relative;
		top: 50%;
	}
	&--center {
		left: 50%;
		transform: translateY(-50%) translateX(-50%);
	}
	&--middle {
		transform: translateY(50%);
	}
}

