a,
.anchor {
  @extend .fx;
  text-decoration: underline;
  cursor: pointer !important;
  &,
  &:active {
    color: $color-theme;
  }
  &:visited
  .is-heir:visited,
  &:visited .is-heir {
    color: inherit;
  }
  &:hover,
  .is-heir:hover,
  &:hover .is-heir {
    color: $color-theme;
  }
  &--shy {
    text-decoration: none;
  }
  &--reset {
    color: inherit;
  }
  &--inverse {
    color: $white;
    &:hover {
      color: $color-dark-blue;
    }
  }
  //.title {
  //  color: $color-body;
  //}
}

.button {
  &,
  &:visited {
    display: inline-block;
    padding: rhythm(2) rhythm(4);
    border: 1px solid;
    background: transparent;
    color: $color-button;
    font: 600 #{px-to-pt( 8px )}/#{px-to-pt( rhythm(2) )} $font-family--meta;
    text-decoration: none;
    text-transform: uppercase;
    letter-spacing: $letter-spacing;
    outline: 0;
  }
  &:hover {
    color: $color-anchor;
  }
  &:active {
    color: $color-dark-blue;
  }
  &--stretch {
    width: 100%;
    text-align: center;
  }
  &--inverse {
    color: $white;
  }
}

.fld {
  border: 0;
  border-bottom: 1px dashed;
  background: transparent;
    &:focus {
        outline: 1px solid mix($white, $color-theme, 80%);
        background: mix($white, $color-theme, 80%);
        .well & {
            outline-color: $white;
            outline-color: rgba($white, 20%);
            background: $white;
            background: rgba($white, 20%);
        }
    }
}
.fld {
  margin: px-to-pt(rhythm(1)) 0;
  padding: px-to-pt(rhythm(1)) 0;
}

/*
 * 2. MSIE
 */

.list {
  &--reset {
    margin: 0;
    padding: 0;
    list-style: none;
  }
  &--inline {
    li {
      display: inline;
    }
  }
  &--float {
    @include clearfix;
    li {
      float: left;
    }
  }
  &--tools {
    &__section {
      margin-top: px-to-pt(rhythm(8));
      .title--em {
        display: block;
        position: absolute;
        left: 0;
        right: 0;
        bottom: 100%;
        padding: px-to-pt(rhythm(2)) 0 px-to-pt(rhythm(2)) px-to-pt(.5 * $gutter-width);
        background: rgba($color-body, .2);
      }
    }
    li {
      position: relative;
      padding: px-to-pt(rhythm(2)) 0 px-to-pt(rhythm(2)) px-to-pt(1.25 * $gutter-width);
      width: 100%; /* 2 */
      &:before,
      &:after {
          content: "";
          position: absolute;
          top: 1em;
          left: px-to-pt(.5 * $gutter-width);
          display: block;
          width: .8em;
          height: .8em;
      }
      &:before {
        background-color: $color-body; /* or whatever color you prefer */
      }
      &:after {
        background-color: $white;
      }
      @for $i from 20 through 1 {
        &:nth-child(#{$i}):after {
          $_opacity: $i * .05;
          opacity: $_opacity;
        }
        // li:nth-child(#{$i}):after {
        //   $_opacity: $i * 5%;
        //     @debug $_opacity;
        //   background: rgba($white, $_opacity);
        // }
      }

    }

  }
  &--discover {
    li {
      &:before {
        background: $color-coral; /* or whatever color you prefer */
      }
    }
  }
  &--concept {
    li {
      &:before {
        background: $color-green; /* or whatever color you prefer */
      }
    }
  }
  &--design {
    li {
      &:before {
        background: $color-yellow; /* or whatever color you prefer */
      }
    }
  }
  &--build {
    li {
      &:before {
        background: $color-teal; /* or whatever color you prefer */
      }
    }
  }
}

@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
  .well {
    .list--inline {
      .grid__cell {
        display: inline-block !important;
        float: left;
      }
    }
  }
  .list--inline {
    display: inline-block;
    float: left;
  }
  li {
    float: left;
  }
}

.brand {
  height: px-to-pt( rhythm(12) );
  background: no-repeat center;
  background-image: url("/images/logos/Brickson-Logotype-RGB-LB--tablet--x2.png");
  background-image: url("/images/logos/Brickson-Logotype-RGB-LB.svg");
  background-size: contain;
  &--ams,
  &--dnb {
    height: px-to-pt( rhythm(15) );
  }
  &--ams {
    background-image: url("/images/logos/Brickson-Logotype-AMS-RGB-LB--tablet--x2.png");
    background-image: url("/images/logos/Brickson-Logotype-AMS-RGB-LB.svg");
  }
  &--dnb {
    background-image: url("/images/logos/Brickson-Logotype-DNB-RGB-LB--tablet--x2.png");
    background-image: url("/images/logos/Brickson-Logotype-DNB-RGB-LB.svg");
  }
  &--handshake {
      height: px-to-pt( rhythm(22) );
  }
  &--cover,
  &--inverse {
    background-image: url("/images/logos/Brickson-Logotype-RGB-DB--tablet--x2.png");
    background-image: url("/images/logos/Brickson-Logotype-RGB-DB.svg");
  }
  @include breakpoint($gt-large-handheld) {
    height: px-to-pt( rhythm(15) );
    &--ams,
    &--dnb {
      height: px-to-pt( rhythm(17) );
    }
    &--handshake {
      height: px-to-pt( rhythm(20) );
    }
    &--handshake {
      position: absolute;
      width: 100%;
      top: 33%;
      left: 0;
      margin-top: 0%;
      transform: translateY(-50%);
    }
  }
  @include breakpoint($gt_small_desktop,$landscape) {
    &--ams,
    &--dnb {
      height: px-to-pt( rhythm(18) );
    }
  }
  @include breakpoint($gt_medium_desktop,$landscape) {
    &--ams,
    &--dnb {
      height: px-to-pt( rhythm(20) );
    }
  }
}

.isolation {
  outline: 1px solid $white;
  outline-offset: 0;
}

.seed {
  display: flex;
  flex-direction: column;
  &__header,
  &__body,
  &__footer {
  }
  &__body,
  &__footer {
    flex-grow: 1;
  }
  &__header {
    height: rhythm(20);
    flex-basis: rhythm(20);
    flex-shrink: 1;
  }
  &__body {
  }
  &__footer {
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
  }
}

.image {
  display: block;
