    width: 100%;
    height: auto;
  }
}

.well {
  background: $color-body;
  color: $white;
  &--stork {
    color: $color-body;
    background: mix($color-teal, $white, 26%);
  }
}

/*
 * 1. Center image
 * 2. Text on an image backdrop is white
 */
.cover {
  // background-color: $color-blue; // Debug
  position: relative;
  overflow: hidden;
  background-color: transparent !important;
  &--light {
    // color: $color-dark-blue;
    // .button {
    //   color: $color-dark-blue;
    //   border-color: $color-dark-blue;
    // }
  }
  &--neutral {
    // color: $color-body;
    // .button {
    //   color: $color-body;
    //   border-color: $color-body;
    // }
  }
  &--hero {

  }
  &__image {
    background-color: #FFF;
    object-fit: cover;
    position: absolute;
    left: 0;
    top: 0;
    z-index: -1;
    width: auto;
    height: 100%;
    min-width: 100%;
    min-height: 100%;
    // max-width: 100%;
    // max-height: 100%;

    // top: 50%; /* 1 */
    // left: 50%; /* 1 */
    // transform: translateY(-50%) translateX(-50%); /* 1 */
  }
  // img {
  //   max-width: 100%;
  // }

  @include breakpoint($portrait) {
    &--hero &__image {
      width: 100%;
      height: auto;
      min-height: 0;
      top: auto;
      bottom: 0;
    }
  }
  @include breakpoint($landscape) {
    &--hero &__image {
        height: auto;
        max-height: 100%;
        left: auto;
        right: 0;
        top: auto;
        bottom: 0;
    }
  }
  @include breakpoint("#{$landscape} and (min-aspect-ratio: 16/9)") {
    &--hero &__image {
        top: auto;
        bottom: 0;
    }
  }
  @include breakpoint("(min-aspect-ratio: 16/9)") {
    &--hero &__image {
        height: 100%;
    }
  }
  @include breakpoint($portrait) {
    &--hero &__image {
        max-width: none;
    }
  }
}

.quote {
  position: relative;
  // quotes: "\201C""\201D""\2018""\2019";
  font-style: italic;
  &--l {
    font-size: to-unit(24px, 1em);
    line-height: to-unit(36px, 1, 24px);
  }
  &:before {
    display: block;
    content: open-quote;
  }
  &:after {
    content: no-close-quote;
  }
  &:before,
  &:after {
    font: normal normal 4em/.5 Arial, Helvetica, Gadget, sans-serif;
  }
}

.fx {
	transition-duration: .3s;
	transition-timing-function: ease-in-out;
	transition-property: all;
}

.is-lt-large-handheld,
.is-gt-large-handheld,
.is-narrative {
	position: absolute;
	left: -999em;
	top: -999em; /* IE pushed the text to the bottom of the page without this */
}
.is-rooted {
	position: relative;
}
.is-cleared {
	@include clearfix;
}
.is-lt-large-handheld {
	@include breakpoint($lt-large-handheld) {
		position: static;
	}
}
.is-gt-large-handheld {
	@include breakpoint($gt-large-handheld) {
		position: static;
	}
}

