.post {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  &__header {
    flex: 1 0 100%;
    width: 100%;
  }
  .navbar + & {
    .pod__seed {
      padding-top: rhythm(20);
    }
  }
  &__title,
  &__main h2 {
    font-size: to-unit(24px);
    line-height: to-unit( rhythm(6), 1, 24px);
  }
  &__title,
  &__main h2,
  &__main p {
      margin: rhythm(6) 0;
  }
  &__aside {}
  @include breakpoint($gt-large-handheld) {

  }
  @include breakpoint($gt_small_desktop) {
    flex-direction: row;
    .navbar + & {
      .pod__seed {
        padding-top: rhythm(30);
      }
    }
    &__main,
    &__aside {
      float: left;
    }
    &__main {
      width: percentage(24/36);
    }

    .wings {
      padding-left: percentage(4/24);
      padding-right: percentage(4/24);
    }
    &__header {
      .wings {
        padding-left: percentage(4/36);
        padding-right: percentage(4/36);
      }
    }
    &__title {
      font-size: to-unit(48px);
      line-height: to-unit(rhythm(14), 1, 48px);
    }
    &__lead {
      font-size: to-unit(18px);
      line-height: to-unit(rhythm(8), 1, 18px);
    }

    &__aside {
      width: percentage(12/36);
      .wings {
        padding-left: percentage(1/12);
        padding-right: percentage(1/12);
      }
    }
    &__title,
    &__lead {
      padding-right: percentage(4/30);
    }
  }
  @include breakpoint($gt-medium-desktop) {

    &__title {
      font-size: to-unit(62px);
      line-height: to-unit(rhythm(18), 1, 62px);
    }
    &__lead {
      font-size: to-unit(20px);
      line-height: to-unit(rhythm(8), 1, 20px);
    }
    &__main {
      h2 {
        font-size: to-unit(32px);
        line-height: to-unit( rhythm(12), 1, 32px);
        margin: rhythm(12) 0 rhythm(6) 0;
      }
      p {
          font-size: to-unit(18px);
          line-height: to-unit( rhythm(7), 1, 18px );
      }
    }
    .wings {
      padding-left: percentage(4/24);
      padding-right: percentage(4/24);
    }

    &__header {
      .wings {
        padding-left: percentage(4/36);
        padding-right: percentage(12/36);
      }
    }
    &__title,
    &__lead {
      padding-right: percentage(2/30);
    }
  }
}

.navigation {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1;
  padding: rhythm() percentage(1/12);
  background: rgba($white,.8);
  img {
    display: block; /* Hack - isolate in image class */
  }
  &__home {
    height: rhythm(8);
  }
  &__section-marker {
    line-height: rhythm(8);
    margin-left: 1ex;
  }
  .menu {
    text-align: right;
  }
  @include breakpoint($gt-large-handheld) {
    right: percentage(12/24);
    padding: rhythm(4) percentage(1/12);
  }
  @include breakpoint($gt-small-desktop) {
    padding: rhythm(4) percentage(1/24);
  }
  @include breakpoint($gt-medium-desktop) {
    padding: rhythm(4) percentage(1/36);
    right: percentage(24/36);
  }
}

.hero {
  .pod__seed {
    justify-content: center;
  }
}

.navbar {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1;
  padding: rhythm(2) gutter();
  // height: rhythm(20);
  background: $white;
  background: rgba($white,.8);
  @include breakpoint($gt-small-desktop) {
    padding: rhythm(4) gutter();
  }
}

